def entrar_e_logar():
    navegador.get(url)

    usuario = navegador.find_element('xpath', '//*[@id="root"]/div/div[2]/div[2]/input')
    senha = navegador.find_element('xpath', '//*[@id="root"]/div/div[2]/div[3]/div/input')
